import React, { useState } from 'react';
import axios from 'axios';
import './UploadResources.css';

const UploadResources = () => {
  const [file, setFile] = useState(null);
  const [uploading, setUploading] = useState(false);
  const [downloadURL, setDownloadURL] = useState('');
  const [error, setError] = useState('');

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    if (selectedFile) {
      setFile(selectedFile);
      setError('');
    }
  };

  const handleUpload = async () => {
    if (!file) return;
    
    setUploading(true);
    setError('');
    
    try {
      const formData = new FormData();
      formData.append('file', file);
      
      // Updated endpoint with full URL and error handling
      const response = await axios.post(
        `${process.env.REACT_APP_API_URL || 'http://localhost:5000'}/api/files/upload`, 
        formData, 
        {
          headers: {
            'Content-Type': 'multipart/form-data'
          },
          withCredentials: true
        }
      );
      
      if (response.data && response.data.filename) {
        setDownloadURL(`${process.env.REACT_APP_API_URL || 'http://localhost:5000'}/uploads/${response.data.filename}`);
      }
    } catch (err) {
      console.error('Upload error:', err);
      setError(
        err.response?.data?.message || 
        err.message || 
        'Upload failed. Please try again.'
      );
    } finally {
      setUploading(false);
    }
  };

  return (
    <div className="upload-container">
      <h2>Upload Your Learning Resources</h2>
      
      {error && <p className="error-message">{error}</p>}
      
      <div className="file-upload-box">
        <input 
          type="file" 
          onChange={handleFileChange}
          className="file-input"
          disabled={uploading}
          accept=".pdf,.doc,.docx,.ppt,.pptx,.jpg,.jpeg,.png" 
        />
        {file && (
          <p className="file-name">
            {file.name} ({(file.size / 1024 / 1024).toFixed(2)} MB)
          </p>
        )}

        <button 
          onClick={handleUpload} 
          disabled={uploading || !file}
          className={`upload-btn ${uploading ? 'disabled' : ''}`}
        >
          {uploading ? 'Uploading...' : 'Upload File'}
        </button>
      </div>

      {downloadURL && (
        <div className="success-message">
          <p>File uploaded successfully!</p>
          <a 
            href={downloadURL} 
            target="_blank" 
            rel="noopener noreferrer"
            className="download-link"
          >
            View Uploaded File
          </a>
        </div>
      )}
    </div>
  );
};

export default UploadResources;
