import React, { useState } from 'react';
import './UploadResources.css'; // Import the custom CSS for modern design

const UploadResources = () => {
  const [file, setFile] = useState(null);
  const [uploading, setUploading] = useState(false);
  const [downloadURL, setDownloadURL] = useState('');
  const [error, setError] = useState(''); // To handle errors

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    if (selectedFile) {
      setFile(selectedFile);
      setError(''); // Reset error when a new file is selected
    }
  };

  const handleUpload = () => {
    if (file) {
      setUploading(true);
      const formData = new FormData();
      formData.append('file', file);

      // Send the file to your backend server using fetch (or axios)
      fetch('http://localhost:5000/api/files/upload', {
        method: 'POST',
        body: formData,
      })
        .then((response) => response.json())
        .then((data) => {
          if (data.success) {
            setDownloadURL(data.fileURL); // The URL returned by your backend
            console.log('File uploaded successfully!');
          } else {
            setError('Error uploading file. Please try again later.');
          }
          setUploading(false);
        })
        .catch((error) => {
          console.error('Error uploading file:', error);
          setError('Error uploading file. Please try again later.');
          setUploading(false);
        });
    }
  };

  return (
    <div className="upload-container">
      <h2>Upload Your Learning Resources</h2>
      
      {/* Display error message */}
      {error && <p className="error-message">{error}</p>}
      
      <div className="file-upload-box">
        <input 
          type="file" 
          onChange={handleFileChange} 
          className="file-input"
          disabled={uploading} // Disable input while uploading
        />
        {file && <p className="file-name">{file.name}</p>} {/* Show file name */}

        <button 
          onClick={handleUpload} 
          disabled={uploading || !file} // Disable button if uploading or no file is selected
          className={`upload-btn ${uploading ? 'disabled' : ''}`}
        >
          {uploading ? 'Uploading...' : 'Upload File'}
        </button>
      </div>

      {/* Display the download URL after successful upload */}
      {downloadURL && (
        <div className="success-message">
          <p>File uploaded successfully!</p>
          <a href={downloadURL} target="_blank" rel="noopener noreferrer">
            View Uploaded File
          </a>
        </div>
      )}
    </div>
  );
};

export default UploadResources;
