// src/components/UploadResources.js
import React, { useState } from 'react';
import { storage } from '../firebase'; // Corrected the import path
import { ref, uploadBytes, getDownloadURL } from 'firebase/storage';

const UploadResources = () => {
  const [file, setFile] = useState(null);
  const [uploading, setUploading] = useState(false);
  const [downloadURL, setDownloadURL] = useState('');

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    if (selectedFile) {
      setFile(selectedFile);
    }
  };

  const handleUpload = () => {
    if (file) {
      setUploading(true);
      
      // Create a reference to the location where the file will be stored
      const fileRef = ref(storage, `resources/${file.name}`);

      // Upload the file to Firebase Storage
      uploadBytes(fileRef, file)
        .then(() => {
          console.log('File uploaded successfully!');
          // After upload, get the download URL of the file
          return getDownloadURL(fileRef);
        })
        .then((url) => {
          console.log('Download URL:', url);
          setDownloadURL(url); // Set the download URL for future use
          setUploading(false);
        })
        .catch((error) => {
          console.error('Error uploading file:', error);
          setUploading(false);
        });
    }
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>Upload Resources</h2>

      {/* Input to select a file */}
      <input type="file" onChange={handleFileChange} />
      {file && <p>File selected: {file.name}</p>}

      {/* Upload button */}
      <button onClick={handleUpload} disabled={uploading}>
        {uploading ? 'Uploading...' : 'Upload File'}
      </button>

      {/* Show the download URL if the file is successfully uploaded */}
      {downloadURL && (
        <div>
          <p>File uploaded successfully!</p>
          <a href={downloadURL} target="_blank" rel="noopener noreferrer">
            View Uploaded File
          </a>
        </div>
      )}
    </div>
  );
};

export default UploadResources;
