import React, { useState, useEffect } from 'react';
import './QuizSubject.css';

const shuffleArray = (array) => {
  const newArr = [...array];
  for (let i = newArr.length - 1; i > 0; i--) {
    const j = Math.floor(Math.random() * (i + 1));
    [newArr[i], newArr[j]] = [newArr[j], newArr[i]];
  }
  return newArr;
};

const QuizSubject = ({ category, questions = [], onBack }) => {
  const [shuffledQuestions, setShuffledQuestions] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [selectedAnswer, setSelectedAnswer] = useState('');
  const [score, setScore] = useState(0);
  const [feedback, setFeedback] = useState('');
  const [quizFinished, setQuizFinished] = useState(false);

  useEffect(() => {
    if (questions && questions.length > 0) {
      const shuffled = shuffleArray(questions);
      setShuffledQuestions(shuffled);
      setCurrentIndex(0);
      setScore(0);
      setSelectedAnswer('');
      setFeedback('');
      setQuizFinished(false);
    } else {
      console.error("No questions available");
    }
  }, [questions]);

  const handleSubmit = () => {
    if (!selectedAnswer) return;

    const currentQuestion = shuffledQuestions[currentIndex];
    const isCorrect = selectedAnswer === currentQuestion.answer;

    if (isCorrect) {
      setScore(prev => prev + 1);
      setFeedback('Correct!');
    } else {
      setFeedback('Incorrect!');
    }

    setTimeout(() => {
      const nextIndex = currentIndex + 1;
      if (nextIndex < shuffledQuestions.length) {
        setCurrentIndex(nextIndex);
        setSelectedAnswer('');
        setFeedback('');
      } else {
        setQuizFinished(true);
      }
    }, 1000);
  };

  if (quizFinished) {
    return (
      <div className="quiz-page">
        <h2>{category} Quiz</h2>
        <p>Quiz finished!</p>
        <p>Your Score: {score}</p>
        <button className="back-btn" onClick={onBack}>← Back to Categories</button>
      </div>
    );
  }

  const currentQuestion = shuffledQuestions[currentIndex];
  if (!currentQuestion) return null;

  return (
    <div className="quiz-page">
      <div className="quiz-info-bar">
        <div className="score-display">Score: {score}</div>
      </div>

      <h2 className="quiz-title">{category} Quiz</h2>

      <div className="quiz-question">
        <p><strong>{currentQuestion.question}</strong></p>
        <div className="quiz-options">
          {currentQuestion.options && currentQuestion.options.map((option, i) => (
            <label key={i}>
              <input
                type="radio"
                name="quiz"
                value={option}
                checked={selectedAnswer === option}
                onChange={() => setSelectedAnswer(option)}
              />
              {option}
            </label>
          ))}
        </div>
        <button className="submit-btn" onClick={handleSubmit}>Submit</button>
        {feedback && <p className="feedback">{feedback}</p>}
      </div>

      <button className="back-btn" onClick={onBack}>← Back to Categories</button>
    </div>
  );
};

export default QuizSubject;
