import React, { useEffect, useState } from 'react';
import { db } from '../firebase'; // Import Firestore
import { collection, getDocs } from 'firebase/firestore'; // Firestore functions
import './LearningMaterials.css';

const LearningMaterials = () => {
  const [materials, setMaterials] = useState([]);

  useEffect(() => {
    // Fetch file metadata from Firestore
    const fetchMaterials = async () => {
      const querySnapshot = await getDocs(collection(db, 'learningMaterials'));
      const materialList = querySnapshot.docs.map((doc) => doc.data());
      setMaterials(materialList);
    };

    fetchMaterials();
  }, []);

  return (
    <div className="materials-container">
      <h2>Learning Materials</h2>
      <div className="materials-list">
        {materials.map((material, index) => (
          <div key={index} className="material-item">
            <p className="material-name">{material.name}</p>
            <a href={material.url} className="download-btn" target="_blank" rel="noopener noreferrer">
              Download
            </a>
          </div>
        ))}
      </div>
    </div>
  );
};

export default LearningMaterials;
