import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './LearningMaterials.css';

const LearningMaterials = () => {
  const [materials, setMaterials] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchMaterials = async () => {
      try {
        const response = await axios.get(`${process.env.REACT_APP_API_URL || 'http://localhost:5000'}/api/files`);
        setMaterials(response.data);
        setLoading(false);
      } catch (err) {
        console.error('Error fetching materials:', err);
        setError('Failed to load learning materials');
        setLoading(false);
      }
    };

    fetchMaterials();
  }, []);

  if (loading) return <div>Loading...</div>;
  if (error) return <div className="error-message">{error}</div>;

  return (
    <div className="learning-materials">
      <h2>Learning Materials</h2>
      <div className="materials-list">
        {materials.map((material, index) => (
          <div key={index} className="material-item">
            <h3>{material.originalname}</h3>
            <a
              href={`${process.env.REACT_APP_API_URL || 'http://localhost:5000'}/uploads/${material.filename}`}
              target="_blank"
              rel="noopener noreferrer"
            >
              View Resource
            </a>
          </div>
        ))}
      </div>
    </div>
  );
};

export default LearningMaterials;
