import React, { useState, useEffect } from 'react';
import './QuizSubject.css'; // Import your updated CSS

const QuizSubject = ({ category, questions, onBack }) => {
  const [availableQuestions, setAvailableQuestions] = useState([]);
  const [currentQuestion, setCurrentQuestion] = useState(null);
  const [selectedAnswer, setSelectedAnswer] = useState('');
  const [score, setScore] = useState(0);
  const [questionCount, setQuestionCount] = useState(0);
  const [feedback, setFeedback] = useState('');

  // Shuffle the questions whenever the quiz starts or resets
  useEffect(() => {
    if (questions.length > 0) {
      const shuffledQuestions = [...questions].sort(() => Math.random() - 0.5); // Shuffle questions randomly
      setAvailableQuestions(shuffledQuestions); // Set shuffled questions as the pool
      getNextQuestion(shuffledQuestions); // Get the first question from the shuffled list
    }
  }, [questions]); // Re-run every time the questions change

  // Function to get the next question
  const getNextQuestion = (questionPool) => {
    if (questionPool.length === 0) {
      setCurrentQuestion(null); // No more questions left
      return;
    }

    // Randomly select a question from the pool and remove it from available questions
    const randomIndex = Math.floor(Math.random() * questionPool.length);
    const next = questionPool[randomIndex];
    setCurrentQuestion(next);
    setAvailableQuestions(prev => prev.filter((_, i) => i !== randomIndex)); // Remove question from pool
    setSelectedAnswer(''); // Reset selected answer
    setFeedback(''); // Reset feedback
  };

  const handleSubmit = () => {
    if (!selectedAnswer) return; // Do nothing if no answer is selected

    // Check if the selected answer is correct
    if (selectedAnswer === currentQuestion.answer) {
      setScore(score + 1); // Increment score for correct answer
      setQuestionCount(questionCount + 1); // Increment question count
      setFeedback('Correct!'); // Provide feedback
    } else {
      setFeedback('Incorrect! Your final score is: ' + score); // Feedback on incorrect answer
      setCurrentQuestion(null); // End the quiz on incorrect answer
    }

    // If answer is correct, wait a moment and show next question
    setTimeout(() => {
      if (selectedAnswer === currentQuestion.answer) {
        getNextQuestion(availableQuestions); // Get next question if correct
      }
    }, 1000); // Wait 1 second before showing the next question
  };

  // If there are no questions left or user answers incorrectly, show the results
  if (!currentQuestion) {
    return (
      <div className="quiz-page">
        <h2>{category} Quiz</h2>
        <p>Quiz finished!</p>
        <p>Your Score: {score}</p>
        <button className="back-btn" onClick={onBack}>← Back to Categories</button>
      </div>
    );
  }

  return (
    <div className="quiz-page">
      <h2 className="quiz-title">{category} Quiz</h2>
      <p>Score: {score}</p>
      <p>Question {questionCount + 1}</p>
      <div className="quiz-question">
        <p><strong>{currentQuestion.question}</strong></p>
        <div className="quiz-options">
          {currentQuestion.options.map((option, i) => (
            <label key={i}>
              <input
                type="radio"
                name="quiz"
                value={option}
                checked={selectedAnswer === option}
                onChange={() => setSelectedAnswer(option)}
              />
              {option}
            </label>
          ))}
        </div>
        <button className="submit-btn" onClick={handleSubmit}>Submit</button>
        {feedback && <p className="feedback">{feedback}</p>}
      </div>
      <button className="back-btn" onClick={onBack}>← Back to Categories</button>
    </div>
  );
};

export default QuizSubject;
