import React, { useState } from 'react';
import axios from 'axios';
import './UploadResources.css';  // Make sure you include this CSS file

const UploadResources = () => {
  const [file, setFile] = useState(null);
  const [message, setMessage] = useState('');

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleUpload = async (e) => {
    e.preventDefault();

    if (!file) {
      setMessage('Please select a file to upload.');
      return;
    }

    const formData = new FormData();
    formData.append('file', file);

    try {
      const baseURL = process.env.REACT_APP_API_URL || 'http://localhost:5000';
      const response = await axios.post(`${baseURL}/api/files/upload`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });

      setMessage(response.data.message || 'File uploaded successfully!');
      setFile(null); // Clear the file input
    } catch (error) {
      console.error('Upload error:', error);
      setMessage(
        error.response?.data?.message || 'Something went wrong during file upload.'
      );
    }
  };

  return (
    <div className="upload-container">
      <h2 className="upload-title">Upload Learning Resources</h2>
      <div className="upload-card">
        <form className="upload-form" onSubmit={handleUpload}>
          <input
            type="file"
            className="file-input"
            onChange={handleFileChange}
          />
          <button type="submit" className="upload-btn">Upload</button>
        </form>
        {message && <p className="upload-message">{message}</p>}
      </div>
    </div>
  );
};

export default UploadResources;
