import React, { useState, useEffect } from 'react';
import { db } from '../firebase'; // Import db from firebase.js
import { collection, getDocs } from 'firebase/firestore'; // Firestore functions
import './LearningMaterials.css'; // Add any custom styles

const LearningMaterials = () => {
  const [materials, setMaterials] = useState([]);

  // Fetch resources from Firestore
  useEffect(() => {
    const fetchMaterials = async () => {
      const materialsCollection = collection(db, 'learningMaterials'); // Reference to Firestore collection
      const materialsSnapshot = await getDocs(materialsCollection); // Get documents from Firestore
      const materialsList = materialsSnapshot.docs.map(doc => doc.data());
      setMaterials(materialsList); // Update state with fetched materials
    };
    
    fetchMaterials();
  }, []);

  return (
    <div className="learning-materials">
      <h2>Learning Materials</h2>
      <div className="materials-list">
        {materials.map((material, index) => (
          <div key={index} className="material-item">
            <h3>{material.name}</h3>
            <a href={material.url} target="_blank" rel="noopener noreferrer">
              View Resource
            </a>
          </div>
        ))}
      </div>
    </div>
  );
};

export default LearningMaterials;
