import React, { useState, useEffect } from 'react';
import './QuizSubject.css';

const shuffleArray = (array) => {
  const newArr = [...array];
  for (let i = newArr.length - 1; i > 0; i--) {
    const j = Math.floor(Math.random() * (i + 1));
    [newArr[i], newArr[j]] = [newArr[j], newArr[i]];
  }
  return newArr;
};

const QuizSubject = ({ category, questions, onBack }) => {
  const [availableQuestions, setAvailableQuestions] = useState([]);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0); // Track current question index
  const [selectedAnswer, setSelectedAnswer] = useState('');
  const [score, setScore] = useState(0);
  const [feedback, setFeedback] = useState('');
  const [quizFinished, setQuizFinished] = useState(false);
  const [correctAnswers, setCorrectAnswers] = useState(0); // Track correct answers count

  useEffect(() => {
    if (category && questions[category] && questions[category].length > 0) {
      const shuffled = shuffleArray(questions[category]);
      setAvailableQuestions(shuffled);
      setCurrentQuestionIndex(0);
      setScore(0);
      setSelectedAnswer('');
      setFeedback('');
      setQuizFinished(false);
      setCorrectAnswers(0); // Reset the correct answers count
    }
  }, [category, questions]);

  const getNextQuestion = () => {
    const nextIndex = currentQuestionIndex + 1;
    if (nextIndex >= availableQuestions.length) {
      setQuizFinished(true);
    } else {
      setCurrentQuestionIndex(nextIndex);
      setSelectedAnswer('');
      setFeedback('');
    }
  };

  const handleSubmit = () => {
    if (!selectedAnswer) return;

    const currentQuestion = availableQuestions[currentQuestionIndex];
    if (selectedAnswer === currentQuestion.answer) {
      setScore(prev => prev + 1);
      setCorrectAnswers(prev => prev + 1); // Increment the correct answer count
      setFeedback('Correct!');
      setTimeout(() => {
        getNextQuestion();
      }, 1000);
    } else {
      setFeedback('Incorrect!');
      setQuizFinished(true); // End the quiz when user answers incorrectly
    }

    // If 3 correct answers, end the quiz after a correct answer
    if (correctAnswers + 1 >= 3) { // Add +1 since correctAnswers has already been updated
      setQuizFinished(true);
    }
  };

  if (quizFinished) {
    return (
      <div className="quiz-page">
        <h2>{category} Quiz</h2>
        <p>Quiz finished!</p>
        <p>Your Score: {score}</p>
        <button className="back-btn" onClick={onBack}>← Back to Categories</button>
      </div>
    );
  }

  if (availableQuestions.length === 0 || currentQuestionIndex >= availableQuestions.length) {
    return null; // Ensure we don't try to render when there are no questions
  }

  const currentQuestion = availableQuestions[currentQuestionIndex];

  return (
    <div className="quiz-page">
      <div className="quiz-info-bar">
        <div className="score-display">Score: {score}</div>
      </div>

      <h2 className="quiz-title">{category} Quiz</h2>

      <div className="quiz-question">
        <p><strong>{currentQuestion.question}</strong></p>
        <div className="quiz-options">
          {currentQuestion.options.map((option, i) => (
            <label key={i}>
              <input
                type="radio"
                name="quiz"
                value={option}
                checked={selectedAnswer === option}
                onChange={() => setSelectedAnswer(option)}
              />
              {option}
            </label>
          ))}
        </div>
        <button className="submit-btn" onClick={handleSubmit}>Submit</button>
        {feedback && <p className="feedback">{feedback}</p>}
      </div>

      <button className="back-btn" onClick={onBack}>← Back to Categories</button>
    </div>
  );
};

export default QuizSubject;
