import React from 'react';
import './QuizSection.css'; // CSS for styling
import quizData from '../data/questions'; // ✅ Import question data

const categories = ['STEM', 'ABM', 'HUMSS', 'TVL-ICT', 'TVL-FBS/COOKERY'];

// ✅ Utility function to get random questions
const getRandomQuestions = (category, count = 3) => {
  const questions = quizData[category] || [];
  const shuffled = [...questions].sort(() => 0.5 - Math.random());
  return shuffled.slice(0, count);
};

const QuizSection = ({ onSelectCategory }) => {
  const handleCategoryClick = (category) => {
    const randomQuestions = getRandomQuestions(category); // ✅ get random questions
    onSelectCategory(category, randomQuestions); // ✅ pass them to parent
  };

  return (
    <div className="quiz-container">
      <h2>Select a Quiz Category</h2>
      <div className="category-grid">
        {categories.map((category, index) => (
          <div 
            key={index} 
            className="category-card"
            onClick={() => handleCategoryClick(category)} // ✅ updated to use new function
          >
            {category}
          </div>
        ))}
      </div>
    </div>
  );
};

export default QuizSection;
