import React, { useState } from 'react';
import axios from 'axios'; // Add axios for API calls
import './UploadResources.css';

const UploadResources = () => {
  const [file, setFile] = useState(null);
  const [uploading, setUploading] = useState(false);
  const [downloadURL, setDownloadURL] = useState('');
  const [error, setError] = useState('');

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    if (selectedFile) {
      setFile(selectedFile);
      setError('');
    }
  };

  const handleUpload = async () => {
    if (!file) return;
    
    setUploading(true);
    setError('');
    
    try {
      const formData = new FormData();
      formData.append('file', file);
      
      // Send file to backend API
      const response = await axios.post('/api/files/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });
      
      console.log('File uploaded successfully!', response.data);
      setDownloadURL(`/uploads/${response.data.filename}`); // URL to access the file
      setUploading(false);
    } catch (err) {
      console.error('Error uploading file:', err);
      setError(err.response?.data?.message || 'Error uploading file. Please try again later.');
      setUploading(false);
    }
  };

  return (
    <div className="upload-container">
      <h2>Upload Your Learning Resources</h2>
      
      {error && <p className="error-message">{error}</p>}
      
      <div className="file-upload-box">
        <input 
          type="file" 
          onChange={handleFileChange} 
          className="file-input"
          disabled={uploading}
        />
        {file && <p className="file-name">{file.name}</p>}

        <button 
          onClick={handleUpload} 
          disabled={uploading || !file}
          className={`upload-btn ${uploading ? 'disabled' : ''}`}
        >
          {uploading ? 'Uploading...' : 'Upload File'}
        </button>
      </div>

      {downloadURL && (
        <div className="success-message">
          <p>File uploaded successfully!</p>
          <a href={downloadURL} target="_blank" rel="noopener noreferrer">
            View Uploaded File
          </a>
        </div>
      )}
    </div>
  );
};

export default UploadResources;
