import React, { useState, useEffect } from 'react';
import './QuizSubject.css'; // Import your updated CSS

const QuizSubject = ({ category, questions, onBack }) => {
  const [availableQuestions, setAvailableQuestions] = useState([]); // To store remaining questions
  const [askedQuestions, setAskedQuestions] = useState([]); // To track already asked questions
  const [currentQuestion, setCurrentQuestion] = useState(null); // To store the current question
  const [selectedAnswer, setSelectedAnswer] = useState(''); // To store the selected answer
  const [score, setScore] = useState(0); // To track the score
  const [questionCount, setQuestionCount] = useState(0); // To track question number
  const [feedback, setFeedback] = useState(''); // To display feedback (Correct/Incorrect)
  const [quizFinished, setQuizFinished] = useState(false); // To track quiz finish state

  // Shuffle questions whenever the quiz starts or resets
  useEffect(() => {
    if (questions.length > 0) {
      const shuffledQuestions = [...questions].sort(() => Math.random() - 0.5);
      setAvailableQuestions(shuffledQuestions);
      setAskedQuestions([]); // Reset asked questions at the start
      setQuizFinished(false); // Reset quiz finish state
      getNextQuestion(shuffledQuestions); // Get the first question
    }
  }, [questions]);

  // Function to get the next question
  const getNextQuestion = (questionPool) => {
    if (questionPool.length === 0) {
      setQuizFinished(true);
      return;
    }

    const randomIndex = Math.floor(Math.random() * questionPool.length);
    const nextQuestion = questionPool[randomIndex];

    setAskedQuestions((prevAsked) => [...prevAsked, nextQuestion]);

    const newPool = questionPool.filter((_, i) => i !== randomIndex);
    setAvailableQuestions(newPool);
    setCurrentQuestion(nextQuestion);
    setSelectedAnswer('');
    setFeedback('');
  };

  // Function to handle submitting the answer
  const handleSubmit = () => {
    if (!selectedAnswer) return;

    const isCorrect = selectedAnswer === currentQuestion.answer;

    if (isCorrect) {
      setScore(score + 1);
      setQuestionCount(questionCount + 1);
      setFeedback('Correct!');

      setTimeout(() => {
        const updatedPool = availableQuestions.filter(q => q !== currentQuestion);
        getNextQuestion(updatedPool);
      }, 1000);
    } else {
      setFeedback('Incorrect!');
      setQuizFinished(true);
    }
  };

  if (quizFinished) {
    return (
      <div className="quiz-page">
        <h2>{category} Quiz</h2>
        <p>Quiz finished!</p>
        <p>Your Score: {score}</p>
        <button className="back-btn" onClick={onBack}>← Back to Categories</button>
      </div>
    );
  }

  if (!currentQuestion) {
    return null;
  }

  return (
    <div className="quiz-page">
      <div className="quiz-status">
        <div className="quiz-score-box">Score: {score}</div>
        <div className="quiz-question-number">Question {questionCount + 1}</div>
      </div>

      <h2 className="quiz-title">{category} Quiz</h2>
      <div className="quiz-question">
        <p><strong>{currentQuestion.question}</strong></p>
        <div className="quiz-options">
          {currentQuestion.options.map((option, i) => (
            <label key={i}>
              <input
                type="radio"
                name="quiz"
                value={option}
                checked={selectedAnswer === option}
                onChange={() => setSelectedAnswer(option)}
              />
              {option}
            </label>
          ))}
        </div>
        <button className="submit-btn" onClick={handleSubmit}>Submit</button>
        {feedback && <p className="feedback">{feedback}</p>}
      </div>
      <button className="back-btn" onClick={onBack}>← Back to Categories</button>
    </div>
  );
};

export default QuizSubject;
