import React, { useState } from 'react';
import { getStorage, ref, uploadBytes, getDownloadURL } from 'firebase/storage'; // Import storage methods from Firebase
import app from '../firebase'; // Import the default exported app instance

const UploadResources = () => {
  const [file, setFile] = useState(null);
  const [uploading, setUploading] = useState(false);
  const [downloadURL, setDownloadURL] = useState('');

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    if (selectedFile) {
      setFile(selectedFile);
    }
  };

  const handleUpload = () => {
    if (file) {
      setUploading(true);

      // Get a reference to the Firebase Storage service using the app instance
      const storage = getStorage(app); // Get storage using the default app instance
      const fileRef = ref(storage, `resources/${file.name}`); // Specify path in Firebase Storage

      // Upload file
      uploadBytes(fileRef, file)
        .then(() => {
          console.log('File uploaded successfully!');
          // Get the download URL
          return getDownloadURL(fileRef);
        })
        .then((url) => {
          console.log('Download URL:', url);
          setDownloadURL(url); // Save the download URL
          setUploading(false);
        })
        .catch((error) => {
          console.error('Error uploading file:', error);
          setUploading(false);
        });
    }
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>Upload Resources</h2>

      <input type="file" onChange={handleFileChange} />
      {file && <p>File selected: {file.name}</p>}

      <button onClick={handleUpload} disabled={uploading}>
        {uploading ? 'Uploading...' : 'Upload File'}
      </button>

      {downloadURL && (
        <div>
          <p>File uploaded successfully!</p>
          <a href={downloadURL} target="_blank" rel="noopener noreferrer">
            View Uploaded File
          </a>
        </div>
      )}
    </div>
  );
};

export default UploadResources;
